import React, {useEffect, useState} from 'react';
import {useAuth} from './../../firebase/auth';
import axios from 'axios';
import {Chart} from 'react-google-charts';

export default function Monthly() {

    const [chartData, setChartData] = useState([['Month', 'Carbon emission in kgs']]);

    const { currentUser } = useAuth();


    if (currentUser && currentUser.uid) {
        let data = {uid: currentUser.uid};
        axios.post('http://localhost:8080/monthly', data)
            .then(response => {
                function compare(a, b) {
                    if (a[0] < b[0]) {
                      return -1;
                    }
                    if (a[0] > b[0]) {
                      return 1;
                    }
                    return 0;
                }
                var graphdata = [];
                for (var i = 0; i < 31; i++) {
                    graphdata.push(i + 1, 0)
                }
                console.log(response.data);
                for (var i of response.data) {
                    graphdata.push([i.date, i.total])
                }
                console.log(graphdata);
                graphdata.sort(compare);
                graphdata.unshift(['Month', 'Carbon emission in kgs']);
                setChartData(graphdata);
            })
            .catch(err => console.log('error --> ', err));

    }

    return (
        <div style={{ display: 'flex', maxWidth: 1200 }}>
            <Chart
                width={1000}
                height={450}
                chartType="ColumnChart"
                loader={<div>Loading Chart</div>}
                data={chartData}
                options={{
                title: 'Monhtly Carbon Emission',
                chartArea: { width: '70%' },
                hAxis: {
                    title: 'Month',
                    minValue: 0,
                },
                vAxis: {
                    title: 'Carbon Emission in kgs',
                },
                }}
                legendToggle
            />

            </div>
    )
};
