import React, {useEffect, useState} from 'react';
import {useAuth} from './../../firebase/auth';
import axios from 'axios';
import {Chart} from 'react-google-charts';

export default function Daily() {

    const [chartData, setChartData] = useState([['Day', 'Carbon emission in kgs']]);

    const { currentUser } = useAuth();


    if (currentUser && currentUser.uid) {
        let data = {uid: currentUser.uid};
        axios.post('http://localhost:8080/daily', data)
            .then(response => {
                function compare(a, b) {
                    if (a[0] < b[0]) {
                      return -1;
                    }
                    if (a[0] > b[0]) {
                      return 1;
                    }
                    return 0;
                }
                var graphdata = [];
                console.log(response.data);
                for (var i of response.data) {
                    graphdata.push([i.date, i.total])
                }
                console.log(graphdata);
                graphdata.sort(compare);
                graphdata.unshift(['Day', 'Carbon emission in kgs']);
                setChartData(graphdata);
            })
            .catch(err => console.log('error --> ', err));

    }


    // useEffect(() => {
    //     setFlightEm(90 * flightTime);
    // }, [flightTime]);

    // useEffect(() => {
    //     setElecEm((0.9 * electricity) / peopleCount);
    // }, [electricity, peopleCount]);

    // function saveEm() {
    //     let data = {
    //         uid: currentUser.uid,
    //         flights: flightEm,
    //         electricity: elecEm,
    //         total: (Number(flightEm) + Number(elecEm))
    //     };
    //     axios.post('http://localhost:8080/monthly', data)
    //         .then(response => console.log(response))
    //         .catch(err => console.log('error --> ', err));
    // }

    return (
        <div style={{ display: 'flex', maxWidth: 900 }}>

        <Chart
        width={'800px'}
        height={'400px'}
        chartType="LineChart"
        loader={<div>Loading Chart</div>}
        data={chartData}
        options={{
            hAxis: {
            title: 'Day',
            },
            vAxis: {
            title: 'Carbon Emission in kgs',
            },
        }}
        rootProps={{ 'data-testid': '1' }}
        />
        </div>
    )
};
