import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function PictureEm() {

    const [src, setSrc] = useState('');

    var loadFile = function(event) {
        var image = document.getElementById('me');
        image.src = URL.createObjectURL(event.target.files[0]);
    };

    function imgUpload(event) {
        try {
            setSrc(URL.createObjectURL(event.target.files[0]));
        } catch(err) {
            console.log(err);
        }
        document.getElementById('abc').src = URL.createObjectURL(event.target.files[0]);
    }

    function calcEm() {
        if (src) {
            let data = {url: src};
            axios.post('http://localhost:8080/img', data)
                .then(res => console.log(res.data))
                .catch(err => console.log('User not found'));
        }
    }

    return (
        <div>
            <br/>
            <form>
                <input type="file"  accept="image/*" name="image" id="file" onInput={imgUpload} />
                <br/>
                <p>{src}</p>
                <br/>
                <img id='abc' src='' />
                <button type="button" onClick={calcEm}>Find Emission</button>              
            </form>
        </div>
    )
};
