    const { currentUser } = useAuth();

    useEffect(() => {
        if (currentUser && currentUser.uid) {
            data = {uid: currentUser.uid};
            axios.get('http://localhost:8080/daily', data)
            .then(response => console.log(response))
            .catch(err => console.log('error --> ', err));

        }
    }, []);

