import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function PictureEm() {

    const [src, setSrc] = useState('');

    var loadFile = function(event) {
        var image = document.getElementById('me');
        image.src = URL.createObjectURL(event.target.files[0]);
    };

    function imgUpload(event) {
        try {
            setSrc(URL.createObjectURL(event.target.files[0]));
        } catch(err) {
            console.log(err);
        }
        var canvas = document.getElementById('canvas');
        var ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        var img = new Image();

        function createImage() {
            img.src = URL.createObjectURL(event.target.files[0]);

            var imgData = ctx.getImageData(0, 0, canvas.width, canvas.height).data;
            console.log(ImageData[0]);
        }

        img.onload = function(createImage) {
            var height = img.height;
            var width = img.width;
            canvas.height = height;
            canvas.width = width;
            ctx.drawImage(img, 0, 0);
            createImage();
        }


        // console.log(imgData[0]);
        // console.log(imgData);

    }

    function calcEm() {
        if (src) {
            console.log(src.slice(5,));
            let data = {url: src};
            console.log(data);
            axios.post('http://localhost:8080/img', data)
                .then(res => console.log(res.data))
                .catch(err => console.log(err));
        }
    }

    return (
        <div>
            <br/>
            <form>
                <input type="file"  accept="image/*" name="image" id="file" onInput={imgUpload} />
                <br/>
                <p>{src}</p>
                <br/>
                <canvas id='canvas'></canvas>
                <button type="button" onClick={calcEm}>Find Emission</button>              
            </form>
        </div>
    )
};
